  /// MENU
   function activerMenu() {
    console.log('activerMenu' );
    var nav = document.getElementById('zone-menu');
    var menu = document.getElementById('menu-logo');

    if(menu){
      var menuLinks = document.getElementById('menu-links');
      var menuItemAccueil = document.getElementById('menu-accueil');
      menu.addEventListener('click', menuOpen );
      window.addEventListener("scroll" , scrollAtTop ); //menuClose );

    //  if (nav.classList.contains('init-close')) menuClose();
    }

    function menuOpen(evt) {
      if(menu){
        if(evt) evt.preventDefault() ;
        if  (menuLinks.classList.contains('invisible')){
          menu.removeEventListener('click', menuOpen );
          // classes
          menuLinks.classList.remove('invisible') ;
          menu.classList.remove('menu-logo-hover') ;
          menu.classList.add('menu-open') ;
          menuItemAccueil.classList.add('accueil') ;
        }
      }
    }

    function scrollAtTop(evt) {
      if (window.pageYOffset <30){
        menuOpen(evt);
      } else{
        menuClose();
      }
      // console.log(window.pageYOffset);
    }

    function menuClose() {
      if(menu){
        if  (!menuLinks.classList.contains('invisible')){
          var timeoutID = window.setTimeout(function () {
            menu.addEventListener('click', menuOpen );
            // classes
            menuLinks.classList.add('invisible') ;
            menu.classList.remove('menu-open');
            menu.classList.add('menu-logo-hover');
            menuItemAccueil.classList.remove('accueil') ;
            console.log('Menu fermÃ©');
          }, 500) ;
        }
      }
    }
  //  console.log('activerMenu.menuClose', activerMenu.menuClose());

  return {
    menuOpen: menuOpen,
    menuClose:  menuClose
  };
} ; // ();



  /*
  contact :
  clic sur les icones contect = retirer close ;
  clic sur close : ajouter close.
  -> sans js actif, le formulaire doit apparaitre en bas de page.
  -> ajouter close au chargement pour le masquer.
  -> il faut aussi ajouter son positionnement quand il est ouvert : sans js, en bas de page ; avec js ,en milieu de page.

  */
  // FORMULAIRE DE CONTACT

  function activerContact() {
    // ajouter close et modal
    var modal = document.getElementById('contact-modal');
    modal.classList.add('close','modal');

    // ajouter les comportements aux boutons contact
    var contacts = document.querySelectorAll('.item-contact') ;
    console.log('contacts',contacts);
    for( var i=0 ; i< contacts.length ; i++){
      // console.log('contacts',contacts[i]);
      contacts[i].addEventListener('click', setContactOpen);
    }
    // activer le bouton close
    setContactClose();

    function setContactOpen() {
      activerMenu.menuClose () ;
      modal.classList.remove('close');
    }

    function setContactClose() {
      var close = document.getElementById('ico-contact-close');
      if(close){
        console.log("close", close);
        close.addEventListener('click', contactClose );
      }
    }
    function contactClose() {
      modal.classList.add('close');
    }

  }
